import React, { useState, useEffect } from 'react'; // import specific statements
import { Container, Card, CardContent, Typography, Button, CardActions, IconButton, CardMedia } from '@mui/material';
import DeleteIcon from '@mui/icons-material/Delete';
import axios from 'axios';


const ProductList = () => {
  const [products, setProducts] = useState([]); // define the state for storing products

  //implement the get products function
  const fetchProducts = async () => {
    try {
      const apiBaseUrl = process.env.REACT_APP_API_BASE_URL || 'http://localhost:3000';
      const response = await axios.get(`${apiBaseUrl}/api/products`);
      console.log(response.data);
      setProducts(response.data);
    } catch (error) {
      console.error('Error fetching products:', error);
    }
  };

  //implement the delete function
  const handleDelete = async (id) => {
    try {
      const apiBaseUrl = process.env.REACT_APP_API_BASE_URL || 'http://localhost:3000';
      await axios.delete(`${apiBaseUrl}/api/products/${id}`);
      setProducts(currentProducts => currentProducts.filter(product => product.id !== id));
    } catch (error) {
      console.error('Failed to delete product:', error);
    }
  };

  useEffect(() => {
    fetchProducts();
  }, []);

  return (
    <Container sx={{
      display: 'flex',
      flexDirection: 'row',
      flexWrap: 'wrap',
      justifyContent: 'center', // Centers the card container
      alignItems: 'center', // Ensures vertical alignment is centered
      paddingTop: 4, // Adds padding at the top of the container
      paddingBottom: 4 // Adds padding at the bottom of the container
    }}>
      {products.map(product => (
        <Card key={product.id} sx={{
          maxWidth: 300, // Sets a maximum width for each card
          m: 2, // Adds margin around each card
          boxShadow: 3, // Adds shadow for 3D effect
          ':hover': {
            boxShadow: 6, // Enhances shadow on hover for a nice visual effect
          }
        }}>
          <CardMedia
            component="img"
            height="140"
            image={product.imageUrl || 'https://via.placeholder.com/150'}
            alt={product.name}
          />
          <CardContent>
            <Typography gutterBottom variant="h5" component="div">
              {product.name}
            </Typography>
            <Typography variant="body2" color="text.secondary">
              {product.description}
            </Typography>
            <Typography variant="body1" color="text.primary">
              ${product.price}
            </Typography>
          </CardContent>
          <CardActions disableSpacing>
            <IconButton onClick={() => handleDelete(product.id)} aria-label="delete product">
              <DeleteIcon />
            </IconButton>
          </CardActions>
        </Card>
      ))}
    </Container>
  );
};

export default ProductList;
